                    echo $countconn = mysqli_num_rows($result) . ',';
                }
                ?>],
                [

                    <?php
                    for ($i = 1; $i < 13; $i++) {
                        $currentyrMnth = date('Y') . '-0' . $i;
                        $sql = "SELECT * FROM customers WHERE expiredate LIKE '%$currentyrMnth%' AND pop='{$_GET['id']}'";
